document.addEventListener("DOMContentLoaded", function () {
    inicarApp();
});

function inicarApp() {
    validarTarjeta();
    validarCampos();
}

function validarTarjeta() {
    var numeroTarjeta = new Cleave('#numero', {
        creditCard: true,
        delimiter: ' ',
        onCreditCardTypeChanged: function (type) {
            const marcaTarjeta = document.getElementById('')
        }
    });

    var fechaTarjeta = new Cleave('#fecha', {
        date: true,
        datePattern: ['m', 'y']
    });

    var codigoTarjeta = new Cleave('#codigo', {
        blocks: [3],
        uppercase: true
    });
}

function validarCampos() {
    var vacio = true;

    const AllInputs = document.querySelectorAll('.campo--i');
    const button = document.querySelector('#submit');
    // recorre los inputs (innece)
    AllInputs.forEach(input => {
        // escucha si se sale de algun input
        input.addEventListener('input', e => {
            // recorre los cont de los inputs para verificar
            for (let i=0; i<AllInputs.length; i++){
                vacio=false;
                if(AllInputs[i].value===''){
                    vacio=true;
                }
            }
            console.log(vacio);

            if (vacio==false) {
                button.classList.add('bg-on');
                button.classList.remove('bg-off');
            } else {
                button.classList.add('bg-off');
                button.classList.remove('bg-on');
            }
        })
    })




}